body.script:
var svgns = "http://www.w3.org/2000/svg";
var p = document.getElementById("spline");
var d = "";
var i = 0;

function setDot(evt)
{
  var el = document.createElementNS(svgns, "circle");
  el.setAttribute( "cx", evt.clientX );
  el.setAttribute( "cy", evt.clientY );
  el.setAttribute( "r", "3" );
  el.setAttribute( "fill", "red" );
  document.documentElement.appendChild( el );

  if ( 0 == i ) {
    d = "M" + evt.clientX + "," + evt.clientY;
  } else if ( 1 == i ) {
    d += " R" + evt.clientX + "," + evt.clientY;
  } else {
    d += " " + evt.clientX + "," + evt.clientY;
  }
  p.setAttribute("d", d);

  parsePath( p );
  i++;
}
